package weekendAssessment;

public class Operators3 {

	public static void main(String[] args) {      //Decision making with operators
		// TODO Auto-generated method stub       //Checking the boolean conditions by using control statement i.e. if___else.		

		int x = 10; 
		int y = 11;
		
		if(!(x<y)||(x==y))
		{
			System.out.println("Condition is True");
		}
		else
		{
			System.out.println("Condition is False");
		}
	}

}
